namespace Itoris\PendingRegistration\Block\Adminhtml\Settings;

class Status extends \Magento\Config\Block\System\Config\Form\Field
{

    /**
     * Status Field Id
     *
     * @var string
     */
    protected $_fieldStatusId = 'itoris_pendingreg_existing_users_all_users_status';

    /**
     * Validate VAT Button Label
     *
     * @var string
     */
    protected $_vatButtonLabel = 'Set Status';


    /**
     * Set template to itself
     *
     * @return \Magento\Customer\Block\Adminhtml\System\Config\Validatevat
     */
    protected function _prepareLayout()
    {
        parent::_prepareLayout();
        if (!$this->getTemplate()) {
            $this->setTemplate('settings/status.phtml');
        }
        return $this;
    }

    public function getFieldId(){
        return $this->_fieldStatusId;
    }
    /**
     * Get the button and scripts contents
     *
     * @param \Magento\Framework\Data\Form\Element\AbstractElement $element
     * @return string
     */
    protected function _getElementHtml(\Magento\Framework\Data\Form\Element\AbstractElement $element)
    {
        $originalData = $element->getOriginalData();
        $buttonLabel = !empty($originalData['button_label']) ? $originalData['button_label'] : $this->_vatButtonLabel;
        $this->addData(
            [
                'button_label' => __($buttonLabel),
                'html_id' => $element->getHtmlId(),
                'ajax_url' => $this->_urlBuilder->getUrl('pendingregistration/index/existenPost'),
            ]
        );

        return $this->_toHtml();

    }
}